 <div class="table-responsive"> <table id="referrals-table" class="table table-condensed"> <?php if(count($referrals)>0): ?> <thead class=""> <tr class="bg-indigo-600"> <th> <?php echo e(trans('users.username')); ?> </th> <th> <?php echo e(trans('users.email')); ?> </th> <th> <?php echo e(trans('users.name')); ?> </th> <th> Package </th> <th> <?php echo e(trans('users.date_joined')); ?> </th> </tr> </thead> <tbody> <?php $__currentLoopData = $referrals; $__env->addLoop($__currentLoopData); foreach($__currentLoopData as $refs): $__env->incrementLoopIndices(); $loop = $__env->getLastLoop(); ?> <tr class=""> <td> <?php echo e($refs->username); ?> </td> <td> <?php echo e($refs->email); ?> </td> <td> <?php echo e($refs->name); ?> <?php echo e($refs->lastname); ?> </td> <td> <?php echo e($refs->packagename); ?> </td> <td> <?php echo e(date('d M Y',strtotime($refs->created_at))); ?> </td> </tr> <?php endforeach; $__env->popLoop(); $loop = $__env->getLastLoop(); ?> </tbody> <?php else: ?> <tr class=""> <td> <?php echo e(trans('users.no_data_found')); ?> </td> </tr> <?php endif; ?> </table> </div>